      first_name: "",
      last_name: "",
    };
  
    componentDidMount() {
      axios
        .get("/user/getDetails")
        .then(({ data: { first_name, last_name } }) => {
          this.setState({ first_name, last_name });
        })
        .catch((err) => console.log(err));
    }
  
    render() {
   
      const { first_name, last_name } = this.state;
   
