import React, { Component } from "react";
import axios from "axios";
import Siderbar from "../components/sidebar";
import Assetbar from "../components/assetbar";
import SettingsComponent from "../components/Settingscomponent";
import ProfileComponent from "../components/profilecomponent";
export class Settings extends Component {


 

 
  state = {
    first_name: "",
    last_name: "",
  };

  componentDidMount() {
    axios
      .get("/user/getDetails")
      .then(({ data: { first_name, last_name } }) => {
        this.setState({ first_name, last_name });
      })
      .catch((err) => console.log(err));
  }

  render() {
 
    const { first_name, last_name } = this.state;
 
    return (
      <div>
        <div className='tedx-container'>
        <Siderbar active='settings' img='' f={first_name[0]} l={last_name[0]} />
      
        <SettingsComponent />
        <ProfileComponent img=''fI={first_name} l={last_name} f={first_name[0]} l={last_name[0]}/>
        </div>
      
      </div>
    );
  }}
 

export default Settings;

